import React from "react";
import "./TaskEdit.css";
import { tasks } from "../../tasks.js";

function TaskEdit(props) {
  // const { task, type, cat, id } = props.location.state;
  console.log(props);
  const id = props.match.params.id;
  const thisTask = tasks.filter((t) => {
    return t.id == id;
  });
  const { task, cat, type } = thisTask[0];

  return (
    <div>
      <div>
        <h3>Hello from TaskEdit...</h3>
        <p>task: {task}</p>
        <p>cat: {cat}</p>
        <p>type: {type}</p>
        <p>id: {id}</p>
      </div>
      <Sides tasks={tasks} />
    </div>
  );
}

export default TaskEdit;
