import React, { useContext } from "react";
import TasksContext from "../../context/context";
import Sides from "../Sides/Sides";
import "./TaskEdit.css";

function TaskEdit(props) {
  const context = useContext(TasksContext);
  const id = props.match.params.id;
  console.log("my context tasks", context.tasks);

  const thisTask = context.tasks.filter((t) => {
    return t.id == id;
  });
  const { task, cat, type } = thisTask[0];

  return (
    <div>
      <div>
        <h3>Hello from TaskEdit...</h3>
        <p>task: {task}</p>
        <p>cat: {cat}</p>
        <p>type: {type}</p>
        <p>id: {id}</p>
      </div>
      <Sides tasks={context.tasks} />
    </div>
  );
}

export default TaskEdit;
