import React, { useContext, useState } from "react";
import { Animated } from "react-animated-css";
import Countdown from "react-countdown";
import ReactStopwatch from "react-stopwatch";

import { TasksContext } from "../../context/context";

import "./MainTask.css";
import "./MainTask.anim.css";
import ButtonSmall from "../ButtonSmall/ButtonSmall";

function MainTask(props) {
  const { mainTask, taskSize } = props;
  const { state, dispatch } = useContext(TasksContext);
  const { mainTaskMovement } = state;
  const [isMainTaskVisible, setIsMainTaskVisible] = useState(true);
  const [notesVisible, setNotesVisible] = useState(false);
  const [mainTaskAnim, setMainTaskAnim] = useState({
    anim: "mainTaskDoneSimple",
    duration: 1200,
  });

  let classList = "main-task " + taskSize;
  classList =
    mainTask.color !== "0"
      ? classList.concat(" main-task-color-" + mainTask.color)
      : classList;

  const mainBtnColor = "xxl-done btn-" + mainTask.color;

  switch (mainTaskMovement) {
    case "in":
      classList = classList.concat(" mainTaskIn");
      break;
    case "done":
      classList = classList.concat(" mainTaskDone");
      break;
    case "out":
      classList = classList.concat(" mainTaskOut");
      break;
    default:
      return null;
  }

  const Completionist = () => <span>You missed the deadline!</span>;

  const countdownRenderer = ({ hours, minutes, seconds, completed }) => {
    if (completed) {
      // Render a completed state
      return <Completionist />;
    } else {
      // Render a countdown
      return (
        <div className="countdown">
          <span className="countdown-units">{hours}h</span>
          <span className="countdown-units">{minutes}m</span>
          <span className="countdown-units">{seconds}s</span>
        </div>
      );
    }
  };

  return (
    <div className="main-task-wrapper">
      <div className="main-task-top">
        <h1 className="main-task-h1">do right now</h1>
        <div className="main-task-with-btns">
          <Animated
            animationOut={mainTaskAnim.anim}
            animationOutDuration={mainTaskAnim.duration}
            isVisible={isMainTaskVisible}
          >
            <div className="notes-container">
              <div
                className={classList}
                onClick={() => {
                  setNotesVisible(!notesVisible);
                  // dispatch({
                  //   type: "HIDE_LEFT_SIDE",
                  //   payload: "toggle",
                  // });
                  // dispatch({
                  //   type: "HIDE_RIGHT_SIDE",
                  //   payload: "toggle",
                  // });
                }}
              >
                <p>{mainTask.task}</p>
              </div>
              <div
                className={
                  notesVisible
                    ? "main-task-notes notes-visible"
                    : "main-task-notes"
                }
              >
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolore
                illo consequatur repellat exercitationem quo, quibusdam quas
                deleniti illum earum quod ducimus eos sit. Atque saepe quidem
                quaerat sapiente beatae quam?
                <br />
                Lorem ipsum dolor sit amet consectetur adipisicing elit.
                Accusamus ipsa velit labore? Quod error id dolore quam magnam,
                voluptatum molestiae. Optio cum asperiores facere ut eaque est
                maxime ea? Et. Quae alias autem debitis esse, explicabo
                perspiciatis quidem iure architecto? Quam voluptatum similique
                consectetur doloribus beatae distinctio ex voluptatibus expedita
                adipisci, non, cum ut quisquam inventore, repudiandae tenetur
                corrupti velit? Earum harum sed facilis eum eligendi, asperiores
                dolor aliquid? Rerum eveniet maxime neque magni, doloribus
                veritatis expedita ratione quia inventore maiores distinctio
                repellendus ad totam officiis perspiciatis alias vel
                perferendis? Cumque officia quod voluptates eius harum, et
                debitis labore itaque commodi. Quae praesentium corrupti nostrum
                sunt voluptatem voluptates impedit. Officiis, ad. Maxime
                necessitatibus, nulla odio beatae totam accusantium. Minima,
                quos! Repellendus iste dolor sequi facere mollitia officiis
                facilis magnam itaque! Laudantium libero sed minima delectus
                odio? Ratione modi voluptas consequuntur iusto itaque, animi
                alias aliquid quam assumenda eum quas iste! Perferendis, natus
                ipsam, suscipit culpa quibusdam iste sed neque vero itaque
                excepturi doloribus delectus illo saepe exercitationem,
                consequatur sequi nisi aliquid ratione fugiat quas magni eos et
                officia? Porro, ratione? Quas tenetur eius sunt, quisquam minus
                voluptate? Repudiandae itaque ipsa eaque nihil fuga sed quasi,
                vitae, adipisci provident consectetur esse sunt aspernatur
                veniam nesciunt magni quisquam architecto id blanditiis nobis!
                Fuga, minima, modi eligendi blanditiis, provident cumque amet
                reiciendis facilis pariatur ex nobis magnam maiores? Ipsam,
                placeat ratione aliquam minima accusamus explicabo rerum optio
                quasi, in eaque voluptas, eius nemo. Natus, vitae rerum quaerat
                id quo alias cum illum accusantium non! Dolorem, asperiores,
                maxime aliquid iste consequuntur libero, dolores quod fugiat sed
                magnam animi itaque unde eos dolor sint! Sed. Asperiores
