                exercitationem ad voluptatibus consectetur temporibus? Pariatur,
                aperiam esse?
              </div>
            </div>
          </Animated>
          <div className="main-task-btns">
            <ButtonSmall
              onClick={() => {
                setMainTaskAnim({
                  anim: "mainTaskOut",
                  duration: 1500,
                });
                setIsMainTaskVisible(false);
                setTimeout(() => {
                  dispatch({
                    type: "CLEAR_MAIN_TASK",
                  });
                }, 1450);
                setTimeout(() => {
                  dispatch({
                    type: "HIDE_LEFT_SIDE",
                    payload: "show",
                  });
                  dispatch({
                    type: "HIDE_RIGHT_SIDE",
                    payload: "show",
                  });
                }, 1500);
              }}
              size="extra-large"
              color="xxl-grey"
              title="give up"
              mainColor={mainTask.color}
            />
            <ButtonSmall
              onClick={() => {
                setMainTaskAnim({
                  anim: "mainTaskDoneSimple",
                  duration: 1400,
                });
                setIsMainTaskVisible(false);
                setTimeout(() => {
                  dispatch({
                    type: "MAIN_TASK_DONE",
                    payload: { taskId: mainTask.id, action: "done" },
                  });
                }, 1100);
                setTimeout(() => {
                  dispatch({
                    type: "HIDE_LEFT_SIDE",
                    payload: "show",
                  });
                  dispatch({
                    type: "HIDE_RIGHT_SIDE",
                    payload: "show",
                  });
                }, 1100);
              }}
              size="extra-large"
              color={mainBtnColor}
              title="done"
            />
          </div>
        </div>
      </div>
      <div className="main-task-bottom">
        Time's running out!
        <Countdown
          className="countdown"
          date={Date.now() + 900000}
          renderer={countdownRenderer}
          controlled={false}
        />
      </div>
    </div>
  );
}

export default MainTask;
