import React from "react";
import {
  fetchedTasks,
  fetchedCategories,
  deletedTask,
  fetchedColors,
} from "./tasks";

export const TasksContext = React.createContext({
  tasks: fetchedTasks,
  doneTasks: [],
  categories: fetchedCategories,
  colors: fetchedColors,
  deletedTask: deletedTask,
  justDeleted: false,
  user: "guest",
  currentTask: null,
  lastColor: null,
  lastCategory: null,
  hideLeftSide: false,
  hideRightSide: false,
});
