import React, { useReducer, createContext, useState } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

import "./App.css";
import Topbar from "./components/Topbar/Topbar";
import HomePage from "./containers/Home/Home";
import SettingsPage from "./containers/Settings/Settings";
import AccountPage from "./containers/Account/Account";
import AboutPage from "./containers/About/About";
import ContactsPage from "./containers/Contacts/Contacts";
import TaskEdit from "./components/TaskEdit/TaskEdit";

import { actions, reducer } from "./reducer";
import { fetchedTasks } from "./tasks";

export const StateContext = createContext({});

function App() {
  const [tasks, setTasks] = useState(fetchedTasks);
  const [user, setUser] = useState("dagnis");
  // const [state, dispatch] = useReducer(reducer, { tasks: tasks });
  localStorage.setItem("dorightnowTasks", JSON.stringify(tasks));

  return (
    <StateContext.Provider value={{ tasks, user }}>
      <Router>
        <div className="App">
          <Topbar />
          <Switch>
            <Route path="/task/:id" component={TaskEdit} />
            <Route path="/contacts" component={ContactsPage} />
            <Route path="/about" component={AboutPage} />
            <Route path="/account" component={AccountPage} />
            <Route path="/settings" component={SettingsPage} />
            <Route path="/">
              <HomePage tasks={tasks} />
            </Route>
          </Switch>
        </div>
      </Router>
    </StateContext.Provider>
  );
}

export default App;
