import React, { useEffect, useState } from "react";
import ButtonSmall from "../ButtonSmall/ButtonSmall";
import TaskList from "../TaskList/TaskList";
import NewTask from "../NewTask/NewTask";

const LeftSide = (props) => {
  const { tasks } = props;
  const [isNewTask, setIsNewTask] = useState(false);
  const [allTasks, setTasks] = useState(tasks);

  useEffect(() => {
    console.log(isNewTask ? isNewTask + " new task to be created!" : "nothing");
  }, [isNewTask]);

  useEffect(() => {
    console.log("tasks have changed", allTasks);
    JSON.stringify(localStorage.setItem("dorightnowTasks", allTasks));
    console.log(
      "local storage",
      JSON.parse(localStorage.getItem("dorightnowTasks"))
    );
  }, [allTasks]);

  return (
    <React.Fragment>
      {isNewTask ? (
        <NewTask
          clickHandle={() => {
            setIsNewTask(!isNewTask);
          }}
        />
      ) : null}
      <div className="side left-side">
        <div className="safari-flex-height-fix">
          <div className="side-top">
            <h3 className="side-top-h3 title-drop">Do today</h3>
            <ButtonSmall
              onClick={() => setIsNewTask(!isNewTask)}
              title="add new"
              color="red"
            />
          </div>
        </div>
        <TaskList tasks={tasks} />
        {
          // <div className="safari-flex-height-fix">
          //   <div className="side-top">
          //     <h3 className="side-top-h3">Coming soon...</h3>
          //     <ButtonSmall title="add new" color="grey" />
          //   </div>
          // </div>
          // <TaskList tasks={tasks} />
        }
      </div>
    </React.Fragment>
  );
};

export default LeftSide;
