import React from "react";
import LeftSide from "./LeftSide";
import RightSide from "./RightSide";

import "./Sides.css";

const Sides = (props) => {
  const { tasks } = props;
  return (
    <React.Fragment>
      <LeftSide tasks={tasks}>
        <div className="slide-btn-left">
          <img
            className="arr-left"
            class=""
            src="img/arr-left.png"
            alt="Hide the left sidebar!"
          />
        </div>
      </LeftSide>
      <RightSide tasks={tasks} />
    </React.Fragment>
  );
};

export default Sides;
