import React, { useContext } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

import "./TaskList.css";
import Task from "../Task/Task";
import { TasksContext } from "../../context/context";

const TaskList = (props) => {
  const { listType } = props;
  const { dispatch, state } = useContext(TasksContext);
  let { tasks } = state;
  const filteredTasks = tasks.filter((t) => t.type === listType);

  function handleDragEnd(result) {
    const newTasks = filteredTasks;
    const [reorderedItem] = tasks.splice(result.source.index, 1);
    filteredTasks.splice(result.destination.index, 0, reorderedItem);
    tasks = newTasks;

    // dispatch({ type: "UPDATE_TASK_LIST", payload: newTasks });
  }

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      {tasks && tasks.length > 0 ? (
        <Droppable droppableId={listType}>
          {(provided) => (
            <div
              ref={provided.innerRef}
              {...provided.droppableProps}
              className="task-list"
            >
              {filteredTasks.map((t, index) => {
                let delay = filteredTasks.indexOf(t) + 2;
                return (
                  <Draggable key={t.id} draggableId={t.id} index={index}>
                    {(provided) => (
                      <Task
                        delay={delay}
                        task={t}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        draggableRef={provided.innerRef}
                      />
                    )}
                  </Draggable>
                );
              })}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      ) : listType === "done" ? (
        <h3 className="no-tasks">Nothing done today</h3>
      ) : (
        <h3 className="no-tasks">No tasks added</h3>
      )}
    </DragDropContext>
  );
};

export default TaskList;
