import React, { useContext, useRef, useEffect } from "react";
import { isMobile } from "react-device-detect";
import { useForm, FormProvider, useFieldArray } from "react-hook-form";

import "./TaskAdd.css";
import ButtonSmall from "../ButtonSmall/ButtonSmall";
import { TasksContext } from "../../context/context";
import turtleIcon from "./turtle.svg";
import quickIcon from "./quick.svg";

function TaskAdd(props) {
  let { clickHandle } = props;
  const { dispatch } = useContext(TasksContext);

  let ref = useRef();

  useEffect(() => {
    ref.current.focus();
  });

  // FORM HANDLING BY REACT-HOOK-FORM
  const { register, control, handleSubmit, errors } = useForm();

  const { append } = useFieldArray({
    control,
    name: "test",
  });

  const onSubmit = (data) => {
    console.log("submit data", data);
    dispatch({ type: "ADD_TASK", payload: data });
    clickHandle();
  };
  console.log("task adding errors", errors);

  const handleUserKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      handleSubmit(onSubmit)();
    }
  };

  function doLaterAction(e) {
    console.log("do later event", e);
    // handleSubmit(onSubmit)();
  }

  return (
    <FormProvider>
      <div className="new-task">
        <h2 className="no-tasks no-tasks-bigger">Add something to do...</h2>

        <form className="new-task-form" onSubmit={handleSubmit(onSubmit)}>
          <textarea
            className="textarea task-input"
            type="text"
            placeholder="Add a new task..."
            name="task"
            rows="3"
            onKeyPress={handleUserKeyPress}
            ref={(e) => {
              register(e, { required: true, max: 140, min: 1, maxLength: 600 });
              ref.current = e;
            }}
          />
          <input
            className="task-input"
            autoComplete="off"
            type="text"
            placeholder="Motivation (optional) – €300, trip to Italy, avoid a punch in the face"
            name="motivation"
            ref={register({ maxLength: 420 })}
          />
          <h3 className="form-h3">Select category:</h3>
          <div className="form-category">
            <select
              className="task-input cat-select"
              name="category"
              ref={register}
            >
              <option value="none">None</option>
              <option value="home">Home</option>
              <option value="work">Work</option>
              <option value="andis">Andis</option>
              <option value="marcis">Mārcis</option>
            </select>
            <div className="new-cat">Create New</div>
          </div>
          <h3 className="form-h3">Select color:</h3>
          <input
            className="form-radio"
            name="color"
            type="radio"
            value="none"
            ref={register}
            defaultChecked
          />
          <input
            className="form-radio cat-0"
            name="color"
            type="radio"
            value="0"
            ref={register}
          />
          <input
            className="form-radio cat-a"
            name="color"
            type="radio"
            value="a"
            ref={register}
          />
          <input
            className="form-radio cat-b"
            name="color"
            type="radio"
            value="b"
            ref={register}
          />
          <input
            className="form-radio cat-c"
            name="color"
            type="radio"
            value="c"
            ref={register}
          />
          <input
            className="form-radio cat-d"
            name="color"
            type="radio"
            value="d"
            ref={register}
          />
          <input
            className="form-radio cat-e"
            name="color"
            type="radio"
            value="e"
            ref={register}
          />
          <div className="add-task-btns">
            <ButtonSmall
              onClick={clickHandle}
              title="Cancel"
              color="grey"
              size={isMobile ? "mobile" : "large"}
            />
            <ButtonSmall
              onClick={() => {
                append({ list: "later" });
                doLaterAction();
              }}
              title="Do later"
              color="grey"
              size={isMobile ? "mobile" : "large"}
              icon={turtleIcon}
            />
            <ButtonSmall
              // onClick={handleSubmit(onSubmit())}
              type="submit"
              title="Do today"
              color="red"
              size={isMobile ? "mobile" : "large"}
              icon={quickIcon}
            />
          </div>
        </form>
      </div>
      <div className="new-task-cover"></div>
    </FormProvider>
  );
}

export default TaskAdd;

